mapper.select:

		SELECT *
	FROM (
 	   SELECT rNo, bNo, replyText, replyer, regdate, ROWNUM AS rnum
    FROM (
        SELECT rNo, bNo, replyText, replyer, regdate
        FROM nReply
       	 	WHERE bNo = #{bNo}
       		ORDER BY rNo DESC
    ) WHERE ROWNUM <= #{cri.pageStart} + #{cri.perPageNum}
	) WHERE rnum > #{cri.pageStart}


SELECT COUNT(bNo) AS reply_count
FROM nReply
WHERE bNo = #{bNo}
